    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       
        try{
            PreparedStatement pst = cn.prepareStatement("insert into equipos (idequipo,partidosJugados,goles,clasificacion,liga) values (?,?,?,?,?)");
            pst.setInt(1, Integer.parseInt(jTextField1.getText()));
            pst.setInt(2, Integer.parseInt(jTextField2.getText()));
            pst.setInt(3, Integer.parseInt(jTextField3.getText()));
            pst.setInt(4, Integer.parseInt(jTextField4.getText()));
            pst.setString(5, jTextField5.getText());
            pst.executeUpdate();
            //consultar();        
                    
        }catch(Exception ex){
            System.out.println(ex.getMessage());
    }//GEN-LAST:event_jButton1ActionPerformed
    }
