    private void jMenuItem1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem1ActionPerformed
        int fila = jTable2.getSelectedRow();
        if(fila>=0){
            jTextField1.setText(jTable2.getValueAt(fila, 0).toString());
            jTextField2.setText(jTable2.getValueAt(fila, 1).toString());
            jTextField3.setText(jTable2.getValueAt(fila, 2).toString());
            jTextField4.setText(jTable2.getValueAt(fila, 3).toString());
            jTextField5.setText(jTable2.getValueAt(fila, 4).toString());
            
        }
        else{
        JOptionPane.showMessageDialog(null, "no selecciono fila");
        }
    }//GEN-LAST:event_jMenuItem1ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        try {
            PreparedStatement pst = cn.prepareStatement("UPDATE equipos SET idequipo='"+jTextField1.getText()+"',partidosJugados='"+jTextField2.getText()+"',goles='"+jTextField3.getText()+"',clasificacion='"+jTextField4.getText()+"',liga='"+jTextField5.getText()+"'where idequipo='"+jTextField1.getText()+"'");
            pst.executeUpdate();
            consultar();
        } catch (Exception ex) {
            System.out.println(ex.getMessage());
        }
    
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        int fila = jTable2.getSelectedRow();
        String ideq ="";
        ideq=jTable2.getValueAt(fila,0).toString();
        try {
            PreparedStatement ps = (PreparedStatement) cn.prepareStatement("Delete from equipos where idequipo='"+ideq+"'");
            ps.executeUpdate();
            consultar();
        } catch (SQLException ex) {
            
        }
    
        
        
    }//GEN-LAST:event_jButton2ActionPerformed

